mapper.select:
SELECT member_no, id, email, password, name, role, birthdate, phone, annual_salary
FROM member


        AND (id LIKE CONCAT('%', #{keyword}, '%') OR name LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%'))


        AND role = #{role}


ORDER BY member_no ASC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM member
AND (id LIKE CONCAT('%', #{keyword}, '%') OR name LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%'))
AND role = #{role}

mapper.update:
