mapper.select:
SELECT member_no, id, email, password, name, role, birthdate, annual_salary
FROM member
WHERE email = #{email}

SELECT member_no, id, email, password, name, role, birthdate, annual_salary
FROM member
WHERE id = #{id}

SELECT member_no, id, email, password, name, role, birthdate, annual_salary
FROM member
WHERE member_no = #{memberNo}

SELECT member_no, id, email, password, name, role, birthdate, annual_salary
FROM member
WHERE email = #{email}

mapper.update:
UPDATE member
SET annual_salary = #{annualSalary}
WHERE member_no = #{memberNo}
